package com.wangzhen.adapter.sample.holderimport android.view.ViewGroupimport android.widget.TextViewimport com.wangzhen.adapter.base.RecyclerViewHolderimport com.wangzhen.adapter.sample.Rimport com.wangzhen.adapter.sample.entity.UserEntity/** * UserViewHolder * Created by wangzhen on 2021/5/25 */class UserViewHolder(parent: ViewGroup) : RecyclerViewHolder<UserEntity?>(parent, R.layout.item_user_layout) {    private val tvName: TextView = findViewById(R.id.tv_name)    private val tvAge: TextView = findViewById(R.id.tv_age)    override fun bind() {        tvName.text = mData!!.name        tvAge.text = mData!!.age.toString()    }}